import logo from './logo.svg';
import './App.css';
import { useRef, useState } from 'react';

function App() {
  const inputRef = useRef(null);
  const [image, setImage] = useState("")
  const handleImageClick = () => {
    inputRef.current.click();
  };
  const handleImageChange = (event) => {
    const file = event.target.files[0];
    console.log(file);
    setImage(event.target.files[0]);
  };

  return (
    <div>
    <div onClick={handleImageClick}>
      {image ? (
        <img src={URL.createObjectURL(image)} alt="testing" height={860/2} width={590/2} />
      ):(
        <img src={require('./Sl.png')} alt="testing" height={860/2} width={590/2} />
      )}
      <input type="file" ref={inputRef} onChange={handleImageChange} style={{display: "none"}}></input>
    </div>
    <button className="uploadbtn">Upload</button>
    </div>
  );
}

export default App;
